    /*
     * Get wiki by name
     */
    function get_wiki($name)
    {
        return $this->db->get_where('wiki',array('name'=>$name))->row_array();
    }
        
    /*
     * Get all wiki
     */
    function get_all_wiki()
    {
        $this->db->order_by('name', 'desc');
